import React from 'react';
import { connect } from 'react-redux';
import '../App.css';

const mapStateToProps = state => ({
    listOfRegion: state.listOfRegion,
    region: state.region,
  })

const Search = ({ listOfRegion, dispatch }) => {
  return (
    <div>
      <h5>Rechercher un cirque</h5>
      <select id="region-select" className="select-bar" style={{
        width: '38vw',
        height: '5vh',
        fontSize: '1.15rem',
      }}>
          <option value="">
              ...
        </option>
        {listOfRegion.map(region =>( 
            <option 
            key={region.id}
            id={region.id}
            value={region.name}
            onChange={e => dispatch({type: 'STORE_REGION', region: e.target[0]})}
            >
            {(region.name)}
            </option>))} 
      </select>
    </div>
  );
};

export default connect(mapStateToProps)(Search);
