const initialState = {
    listOfRegion : [],
    region: ""
}

const circusReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'DISPLAY_ALL_REGION':
            return {
                ...state,
                listOfRegion: action.listOfRegion,
            }
        case 'SAVE_REGION':
            return {
                ...state,
                region: action.region,
            }    
        default:
            return state;
    }
}

export default circusReducer;
