import React from 'react';
import { connect } from 'react-redux';
import '../App.css';

const mapStateToProps = state => ({
    listOfRegion: state.listOfRegion,
    region: state.region,
  })

const Search = ({ listOfRegion, dispatch }) => {
  return (
    <div>
      <h5>Rechercher un cirque</h5>
      <select id="region-select" className="select-bar" style={{
        width: '38vw',
        height: '5vh',
        fontSize: '1.15rem',
      }}>
          <option value="">
              ...
        </option>
        {listOfRegion.map(region =>( 
            <option 
            key={region.id}
            id={region.id}
            value={region.name}
            onChange={(e) => dispatch({
               type: 'SAVE_REGION', 
               region: e.target[0].name 
              })}
            >
            {(region.name)}
            </option>
        ))} 
      </select>
      <div className="media">
        <img src="https://cdn5.vectorstock.com/i/1000x1000/73/79/circus-tent-cartoon-vector-7217379.jpg" className="mr-3" alt="..."/>
          <div className="media-body">
            <h6 className="mt-0">Media heading</h6>
                Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.
          </div>
      </div>
    </div>
  );
};

export default connect(mapStateToProps)(Search);
