import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { connect } from 'react-redux';
import NavBar from './Components/NavBar';
import Carousel from './Components/Carousel';
import Footerbar from './Components/FooterBar';
import Search from './Components/search';


class App extends Component {
  componentDidMount() {
    const { dispatch } = this.props;
    axios.get('http://localhost:8000/regions')
      .then(response => dispatch ({type:'DISPLAY_ALL_REGION', listOfRegion: response.data}))
      .catch(err => console.log(err));
    axios.get('http://localhost:8000/circus')
      .then(response => dispatch ({type: 'STORE_CIRCUS_INFOS', circus: response.data}) )
  }
  render(){
    return (
    <div className="App">
      <NavBar />
      <Carousel />
      <Search />
      <Footerbar />
    </div>
    );
  }
}

export default connect()(App);
