import React from 'react';
import { connect } from 'react-redux';
import '../App.css';

const mapStateToProps = state => ({
    listOfRegion: state.listOfRegion,
    region: state.region,
  })

const Search = ({ listOfRegion, dispatch }) => {
  return (
    <div>
      <h5>Rechercher un cirque</h5>
      <select id="region-select" className="select-bar" style={{
        width: '38vw',
        height: '5vh',
        fontSize: '1.15rem',
      }}>
          <option value="">
              ...
        </option>
        {listOfRegion.map(region =>( 
            <option 
            key={region.id}
            id={region.id}
            value={region.name}
            onChange={(e) => dispatch({
               type: 'SAVE_REGION', 
               region: e.target[0].name 
              })}
            >
            {(region.name)}
            </option>
        ))} 
      </select>
      <div className="card">
        <img src="https://s3.amazonaws.com/pix.iemoji.com/images/emoji/apple/ios-12/256/circus-tent.png" className="card-img-top" alt="..."/>
      <div className="card-body">
        <h6 className="card-title">Card title</h6>
          <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
      </div>
</div>
    </div>
  );
};

export default connect(mapStateToProps)(Search);
