import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { connect } from 'react-redux';

const mapStateToProps = state => ({
  listOfCircus: state.listOfCircus,
})

class App extends Component {

  dataFromBack() {
    const { dispatch } = this.props;
    axios.get('http://localhost:8000/circus')
      .then(response => dispatch({ type: 'DISPLAY_ALL_CIRCUS', listOfCircus: response.data}))
  }
  render(){
    const { listOfCircus } = this.props
    return (
    <div className="App">
      <p>{listOfCircus}</p>
    </div>
    );
  }
}

export default connect(mapStateToProps)(App);
